using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;namespace Foundation{    /// <summary>    /// NSExpression is used to represent expressions in a predicate.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/Cocoa/Reference/Foundation/Classes/NSExpression_Class/index.html#//apple_ref/occ/cl/NSExpression"/>    [iOSVersion(3)]    public class NSExpression : NSObject//, AnyObject, NSCoding, NSCopying, NSObjectProtocol, NSSecureCoding    {        /// <summary>        /// Initializes the expression with the specified expression type.        /// </summary>        /// <param name="expressionType">The type of the new expression, as defined by NSExpressionType.</param>        [iOSVersion(3)]        public NSExpression(NSExpressionType expressionType) { }                /// <summary>        /// Initializes the expression with the specified expression format and array of arguments.        /// </summary>        /// <param name="format">The expression format.</param>        /// <param name="argumentArray">An array of arguments to be used with the expressionFormat string.</param>        [iOSVersion(4)]        public NSExpression(string format, AnyObject[] argumentArray) { }                /// <summary>        /// Initializes the expression with the specified expression format and arguments list.        /// </summary>        /// <param name="format">The expression format.</param>        /// <param name="arguments">A list of arguments to be inserted into the expressionFormat string. The argument list is terminated by nil.</param>        [iOSVersion(4)]        public NSExpression(string format, CVaListPointer arguments) { }                /// <summary>        /// Returns a new expression that represents a given constant value.        /// </summary>        /// <param name="forConstantValue">The constant value the new expression is to represent.</param>        [iOSVersion(3)]        public NSExpression(AnyObject forConstantValue) { }                /// <summary>        /// Returns a new expression that represents the object being evaluated.        /// </summary>        [iOSVersion(3)]        [Export("expressionForEvaluatedObject")]        public static NSExpression ExpressionForEvaluatedObject() { return default(NSExpression); }                /// <summary>        /// Returns a new expression that invokes valueForKeyPath: with a given key path.        /// </summary>        /// <param name="forKeyPath">The key path that the new expression should evaluate.</param>        [iOSVersion(3)]        public NSExpression(string forKeyPath) { }                /// <summary>        /// Returns a new expression that extracts a value from the variable bindings dictionary for a given key.        /// </summary>        /// <param name="forVariable">The key for the variable to extract from the variable bindings dictionary.</param>        /// <param name="NAME_YOUR_PARAMS">DO NOT USE THIS PARAMETER - Instead make sure to name the parameters you're using.</param>        [iOSVersion(3)]        [IgnoreParameter("NAME_YOUR_PARAMS")]        public NSExpression(string forVariable, bool NAME_YOUR_PARAMS = false) { }                /// <summary>        /// Returns a new expression that represents any key for a Spotlight query.        /// </summary>        [iOSVersion(7)]        [Export("expressionForAnyKey")]        public static NSExpression ExpressionForAnyKey() { return default(NSExpression); }                /// <summary>        /// Returns a new aggregate expression for a given collection.        /// </summary>        /// <param name="forAggregate">A collection object (an instance of NSArray, NSSet, or NSDictionary) that contains further expressions.</param>        [iOSVersion(3)]        public NSExpression(AnyObject[] forAggregate) { }                /// <summary>        /// Returns a new NSExpression object that represent the union of a given set and collection.        /// </summary>        /// <param name="forUnionSet">An expression that evaluates to an NSSet object.</param>        /// <param name="with">An expression that evaluates to a collection object (an instance of NSArray, NSSet, or NSDictionary).</param>        [iOSVersion(3)]        public NSExpression(NSExpression forUnionSet, NSExpression with) { }                /// <summary>        /// Returns a new NSExpression object that represent the intersection of a given set and collection.        /// </summary>        /// <param name="forIntersectSet">An expression that evaluates to an NSSet object.</param>        /// <param name="with">An expression that evaluates to a collection object (an instance of NSArray, NSSet, or NSDictionary).</param>        /// <param name="NAME_YOUR_PARAMS">DO NOT USE THIS PARAMETER - Instead make sure to name the parameters you're using.</param>        [iOSVersion(3)]        [IgnoreParameter("NAME_YOUR_PARAMS")]        public NSExpression(NSExpression forIntersectSet, NSExpression with, bool NAME_YOUR_PARAMS = false) { }                /// <summary>        /// Returns a new NSExpression object that represent the subtraction of a given collection from a given set.        /// </summary>        /// <param name="forMinusSet">An expression that evaluates to an NSSet object.</param>        /// <param name="with">An expression that evaluates to a collection object (an instance of NSArray, NSSet, or NSDictionary).</param>        /// <param name="NAME_YOUR_PARAMS">DO NOT USE THIS PARAMETER - Instead make sure to name the parameters you're using.</param>        [iOSVersion(3)]        [IgnoreParameter("NAME_YOUR_PARAMS")]        public NSExpression(NSExpression forMinusSet, NSExpression with, int NAME_YOUR_PARAMS = 0) { }                /// <summary>        /// Returns an expression that filters a collection by storing elements in the collection in a given variable and keeping the elements for which qualifier returns true.        /// </summary>        /// <param name="forSubquery">A predicate expression that evaluates to a collection.</param>        /// <param name="usingIteratorVariable">Used as a local variable, and will shadow any instances of variable in the bindings dictionary. The variable is removed or the old value replaced once evaluation completes.</param>        /// <param name="predicate">The predicate used to determine whether the element belongs in the result collection.</param>        [iOSVersion(3)]        public NSExpression(NSExpression forSubquery, string usingIteratorVariable, AnyObject predicate) { }                /// <summary>        /// Creates an NSExpression object that will use the Block for evaluating objects.        /// </summary>        /// <param name="forBlock">The Block is applied to the object to be evaluated.        ///   The Block takes three arguments and returns a value:        ///       evaluatedObject        ///   The object to be evaluated.        ///       expressions        ///   An array of predicate expressions that evaluates to a collection.        ///       context        ///   A dictionary that the expression can use to store temporary state for one predicate evaluation.        ///   Note that context is mutable, and that it can only be accessed during the evaluation of the expression. You must not attempt to retain it for use elsewhere. ]        ///   The Block returns the evaluatedObject.</param>        /// <param name="arguments">An array containing NSExpression objects that will be used as parameters during the invocation of selector.        ///   For a selector taking no parameters, the array should be empty. For a selector taking one or more parameters, the array should contain one NSExpression object which will evaluate to an instance of the appropriate type for each parameter.        ///   If there is a mismatch between the number of parameters expected and the number you provide during evaluation, an exception may be raised or missing parameters may simply be replaced by nil (which occurs depends on how many parameters are provided, and whether you have over- or underflow).        ///   See expressionForFunction:arguments: for a complete list of arguments.</param>        [iOSVersion(4)]        public NSExpression(Func<AnyObject, AnyObject[], NSMutableDictionary, AnyObject> forBlock, AnyObject[] arguments) { }                /// <summary>        /// Returns a new expression that will invoke one of the predefined functions.        /// </summary>        /// <param name="forFunction">The name of the function to invoke.</param>        /// <param name="arguments">An array containing NSExpression objects that will be used as parameters during the invocation of selector.        ///   For a selector taking no parameters, the array should be empty. For a selector taking one or more parameters, the array should contain one NSExpression object which will evaluate to an instance of the appropriate type for each parameter.        ///   If there is a mismatch between the number of parameters expected and the number you provide during evaluation, an exception may be raised or missing parameters may simply be replaced by nil (which occurs depends on how many parameters are provided, and whether you have over- or underflow).</param>        /// <param name="NAME_YOUR_PARAMS">DO NOT USE THIS PARAMETER - Instead make sure to name the parameters you're using.</param>        [iOSVersion(3)]        [IgnoreParameter("NAME_YOUR_PARAMS")]        public NSExpression(string forFunction, AnyObject[] arguments, bool NAME_YOUR_PARAMS = false) { }                /// <summary>        /// Returns an expression which will return the result of invoking on a given target a selector with a given name using given arguments.        /// </summary>        /// <param name="forFunction">An NSExpression object which will evaluate an object on which the selector identified by name may be invoked.</param>        /// <param name="selectorName">The name of the method to be invoked.</param>        /// <param name="arguments">An array containing NSExpression objects which can be evaluated to provide parameters for the method specified by name.</param>        [iOSVersion(3)]        public NSExpression(NSExpression forFunction, string selectorName, AnyObject[] arguments) { }                /// <summary>        /// Evaluates an expression using a given object and context.        /// </summary>        /// <param name="@object">The object against which the expression is evaluated.</param>        /// <param name="context">A dictionary that the expression can use to store temporary state for one predicate evaluation. Can be nil.        ///   Note that context is mutable, and that it can only be accessed during the evaluation of the expression. You must not attempt to retain it for use elsewhere.</param>        [iOSVersion(3)]        [Export("expressionValueWithObject")]        public AnyObject ExpressionValueWithObject(AnyObject @object, NSMutableDictionary context) { return default(AnyObject); }                /// <summary>        /// Force an expression that was securely decoded to allow evaluation.        /// </summary>        [iOSVersion(7)]        [Export("allowEvaluation")]        public void AllowEvaluation() {  }                /// <summary>        /// The arguments for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("arguments")]        public AnyObject[] Arguments { get; private set; }                /// <summary>        /// The collection of expressions in an aggregate expression, or the collection element of a subquery expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("collection")]        public AnyObject Collection { get; private set; }                /// <summary>        /// The constant value of the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("constantValue")]        public AnyObject ConstantValue { get; private set; }                /// <summary>        /// The expression type for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("expressionType")]        public NSExpressionType ExpressionType { get; private set; }                /// <summary>        /// The function for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("function")]        public string Function { get; private set; }                /// <summary>        /// The key path for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("keyPath")]        public string KeyPath { get; private set; }                /// <summary>        /// The operand for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("operand")]        public NSExpression Operand { get; private set; }                /// <summary>        /// The predicate of a subquery expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("predicate")]        public NSPredicate Predicate { get; private set; }                /// <summary>        /// The left expression of an aggregate expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("leftExpression")]        public NSExpression LeftExpression { get; private set; }                /// <summary>        /// The right expression of an aggregate expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("rightExpression")]        public NSExpression RightExpression { get; private set; }                /// <summary>        /// The variable for the expression. (read-only)        /// </summary>        [iOSVersion(3)]        [Export("variable")]        public string Variable { get; private set; }                /// <summary>        /// The block that is executed to evaluate the expression. (read-only)        /// </summary>        [iOSVersion(4)]        [Export("expressionBlock")]        public Func<AnyObject, AnyObject[], NSMutableDictionary, AnyObject> ExpressionBlock { get; private set; }    }    /// <summary>    /// Defines the possible types of NSExpression.    /// </summary>    [iOSVersion(3)]    public enum NSExpressionType    {        /// <summary>        /// An expression that always returns the same value.        /// </summary>        [iOSVersion(3)]        ConstantValueExpressionType,        /// <summary>        /// An expression that always returns the parameter object itself.        /// </summary>        [iOSVersion(3)]        EvaluatedObjectExpressionType,        /// <summary>        /// An expression that always returns whatever value is associated with the key specified by ‘variable’ in the bindings dictionary.        /// </summary>        [iOSVersion(3)]        VariableExpressionType,        /// <summary>        /// An expression that returns something that can be used as a key path.        /// </summary>        [iOSVersion(3)]        KeyPathExpressionType,        /// <summary>        /// An expression that returns the result of evaluating a function.        /// </summary>        [iOSVersion(3)]        FunctionExpressionType,        /// <summary>        /// An expression that creates a union of the results of two nested expressions.        /// </summary>        [iOSVersion(3)]        UnionSetExpressionType,        /// <summary>        /// An expression that creates an intersection of the results of two nested expressions.        /// </summary>        [iOSVersion(3)]        IntersectSetExpressionType,        /// <summary>        /// An expression that combines two nested expression results by set subtraction.        /// </summary>        [iOSVersion(3)]        MinusSetExpressionType,        /// <summary>        /// An expression that filters a collection using a subpredicate.        /// </summary>        [iOSVersion(3)]        SubqueryExpressionType,        /// <summary>        /// An expression that defines an aggregate of NSExpression objects.        /// </summary>        [iOSVersion(3)]        AggregateExpressionType,        /// <summary>        /// An expression that represents any key.        /// </summary>        [iOSVersion(7)]        AnyKeyExpressionType,        /// <summary>        /// An expression that uses a Block.        /// </summary>        [iOSVersion(4)]        BlockExpressionType,    }}