using ObjectiveC;using System;using SwiftSharp.Attributes;using System.Collections.Generic;namespace Foundation{	/// <summary>	/// NSMachPort is a subclass of NSPort that can be used as an endpoint for distributed object connections (or raw messaging). NSMachPort is an object wrapper for a Mach port, the fundamental communication port in OS X. NSMachPort allows for local (on the same machine) communication only. A companion class, NSSocketPort, allows for both local and remote distributed object communication, but may be more expensive than NSMachPort for the local case.	/// </summary>	/// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/Cocoa/Reference/Foundation/Classes/NSMachPort_Class/index.html#//apple_ref/occ/cl/NSMachPort"/>	[iOSVersion(2)]	public class NSMachPort : NSPort//, AnyObject, NSCoding, NSCopying, NSObjectProtocol	{		/// <summary>		/// Creates and returns a port object configured with the given Mach port.		/// </summary>		/// <param name="machPort">The Mach port for the new port. This parameter should originally be of type mach_port_t.</param>		[iOSVersion(2)]		[Export("portWithMachPort")]		public static NSPort PortWithMachPort(uint machPort) { return default(NSPort); }				/// <summary>		/// Creates and returns a port object configured with the specified options and the given Mach port.		/// </summary>		/// <param name="machPort">The Mach port for the new port. This parameter should originally be of type mach_port_t.</param>		/// <param name="options">Specifies options for what to do with the underlying port rights when the NSMachPort object is invalidated or destroyed. For a list of constants, see Mach Port Rights.</param>		[iOSVersion(2)]		[Export("portWithMachPort")]		public static NSPort PortWithMachPort(uint machPort, int options) { return default(NSPort); }				/// <summary>		/// Initializes a newly allocated NSMachPort object with a given Mach port.		/// </summary>		/// <param name="machPort">The Mach port for the new port. This parameter should originally be of type mach_port_t.</param>		[iOSVersion(2)]		[Export("convenience init")]		public NSMachPort(uint machPort) { }				/// <summary>		/// Initializes a newly allocated NSMachPort object with a given Mach port and the specified options.		/// </summary>		/// <param name="machPort">The Mach port for the new port. This parameter should originally be of type mach_port_t.</param>		/// <param name="options">Specifies options for what to do with the underlying port rights when the NSMachPort object is invalidated or destroyed. For a list of constants, see Mach Port Rights.</param>		[iOSVersion(2)]		public NSMachPort(uint machPort, int options) { }				/// <summary>		/// Removes the receiver from the run loop mode mode of runLoop.		/// </summary>		/// <param name="runLoop">The run loop from which to remove the receiver.</param>		/// <param name="forMode">The run loop mode from which to remove the receiver.</param>		[iOSVersion(2)]		[Export("removeFromRunLoop")]		public void RemoveFromRunLoop(NSRunLoop runLoop, string forMode) {  }				/// <summary>		/// Schedules the receiver into the run loop mode mode of runLoop.		/// </summary>		/// <param name="runLoop">The run loop to which to add the receiver.</param>		/// <param name="forMode">The run loop mode in which to add the receiver.</param>		[iOSVersion(2)]		[Export("scheduleInRunLoop")]		public void ScheduleInRunLoop(NSRunLoop runLoop, string forMode) {  }				/// <summary>		/// Returns the receiver’s delegate.		/// </summary>		[iOSVersion(4)]		[Export("delegate")]		public NSMachPortDelegate Delegate() { return default(NSMachPortDelegate); }				/// <summary>		/// Sets the receiver’s delegate to a given object.		/// </summary>		/// <param name="anObject">The delegate for the receiver.</param>		[iOSVersion(4)]		[Export("setDelegate")]		public void SetDelegate(NSMachPortDelegate anObject) {  }				/// <summary>		/// Returns as an int the Mach port used by the receiver.		/// </summary>		[iOSVersion(2)]		[Export("machPort")]		public uint MachPort { get; private set; }				/// <summary>		/// Used to remove access rights to a mach port when the NSMachPort object is invalidated or destroyed.		/// </summary>		public int NSMachPortDeallocateNone { get; set; }	}}