using ObjectiveC;using System;using SwiftSharp.Attributes;namespace Foundation{	//https://developer.apple.com/library/prerelease/ios/documentation/Cocoa/Reference/NSStreamDelegate_Protocol/index.html#//apple_ref/occ/intf/NSStreamDelegate	/// <summary>	/// The NSStreamDelegate protocol defines the optional methods implemented by delegates of NSStream objects.	/// </summary>	[iOSVersion(4)]	public interface NSStreamDelegate	{		/// <summary>		/// The delegate receives this message when a given event has occurred on a given stream.		/// </summary>		/// <param name="theStream">The stream on which streamEvent occurred.</param>		/// <param name="handleEvent">The stream event that occurred.</param>		[iOSVersion(2)]		[Export("stream")]		[Optional]		void Stream(NSStream theStream, NSStreamEvent handleEvent);	}}