using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// NSTextAttachment objects are used by the NSAttributedString class cluster as the values for attachment attributes (stored in the attributed string under the key named NSAttachmentAttributeName). The objects you create with this class are referred to as text attachment objects, or when no confusion will result, as text attachments or merely attachments.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/NSTextAttachment_Class_TextKit/index.html#//apple_ref/occ/cl/NSTextAttachment"/>    [iOSVersion(7)]    public class NSTextAttachment : NSObject//, AnyObject, NSCoding, NSObjectProtocol, NSTextAttachmentContainer    {        /// <summary>        /// Initializes a new NSTextAttachment object with the given data.        /// </summary>        /// <param name="data">Data to use for the text attachment contents. Can be nil.</param>        /// <param name="ofType">A uniform type identifier specifying the data type of the attachment contents. Can be nil.</param>        [iOSVersion(7)]        public NSTextAttachment(NSData data, string ofType) { }                /// <summary>        /// Defines the layout bounds of the receiver's graphical representation in the text coordinate system.        /// </summary>        [iOSVersion(7)]        [Export("bounds")]        public CGRect Bounds { get; set; }                /// <summary>        /// Defines the contents for the text attachment.        /// </summary>        [iOSVersion(7)]        [Export("contents")]        public NSData Contents { get; set; }                /// <summary>        /// Defines the file type of the contents for the text attachment.        /// </summary>        [iOSVersion(7)]        [Export("fileType")]        public string FileType { get; set; }                /// <summary>        /// Image representing the text attachment contents.        /// </summary>        [iOSVersion(7)]        [Export("image")]        public UIImage Image { get; set; }                /// <summary>        /// File wrapper associated with the text attachment.        /// </summary>        [iOSVersion(7)]        [Export("fileWrapper")]        public NSFileWrapper FileWrapper { get; set; }                /// <summary>        /// This character is used to denote an attachment.        /// </summary>        public int NSAttachmentCharacter { get; set; }    }}