using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// An NSTextTab object represents a tab in an NSParagraphStyle object, storing an alignment type and location. NSTextTab objects are used with Text Kit.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/NSTextTab_Class_TextKit/index.html#//apple_ref/occ/cl/NSTextTab"/>    [iOSVersion(7)]    public class NSTextTab : NSObject//, AnyObject, NSCoding, NSCopying, NSObjectProtocol    {        public NSTextTab() { }        /// <summary>        /// Initializes a text tab with the text alignment, location, and options.        /// </summary>        /// <param name="textAlignment"></param>        /// <param name="location"></param>        /// <param name="options"></param>        [iOSVersion(7)]        public NSTextTab(NSTextAlignment textAlignment, CGFloat location, Dictionary<NSObject, AnyObject> options) { }                /// <summary>        /// Returns the column terminators for the given locale.        /// </summary>        /// <param name="aLocale"></param>        [iOSVersion(7)]        [Export("columnTerminatorsForLocale")]        public static NSCharacterSet ColumnTerminatorsForLocale(NSLocale aLocale) { return default(NSCharacterSet); }                /// <summary>        /// The dictionary of optional configuration attributes.        /// </summary>        [iOSVersion(7)]        [Export("options")]        public Dictionary<NSObject, AnyObject> Options { get; private set; }                /// <summary>        /// The location of the tab stop inside the line fragment rectangle coordinate system.        /// </summary>        [iOSVersion(7)]        [Export("location")]        public CGFloat Location { get; private set; }                /// <summary>        /// The aligment of tab column contents.        /// </summary>        [iOSVersion(7)]        [Export("alignment")]        public NSTextAlignment Alignment { get; private set; }    }}