using CoreGraphics;
using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/NSTextTab_Class_TextKit/index.html#//apple_ref/occ/cl/NSTextTab	/// <summary>	/// An NSTextTab object represents a tab in an NSParagraphStyle object, storing an alignment type and location. NSTextTab objects are used with Text Kit.	/// </summary>	[iOSVersion(7)]	public class NSTextTab : NSObject	{		/// <summary>		/// Initializes a text tab with the text alignment, location, and options.		/// </summary>		[iOSVersion(7)]		public NSTextTab(NSTextAlignment textAlignment, CGFloat location, NSDictionary options) { }				/// <summary>		/// Returns the column terminators for the given locale.		/// </summary>		[iOSVersion(7)]		[Export("columnTerminatorsForLocale")]		public static NSCharacterSet ColumnTerminatorsForLocale(NSLocale aLocale) { return null; }				/// <summary>		/// The dictionary of optional configuration attributes.		/// </summary>		[iOSVersion(7)]		[Export("options")]		public NSDictionary Options { get; private set; }				/// <summary>		/// The location of the tab stop inside the line fragment rectangle coordinate system.		/// </summary>		[iOSVersion(7)]		[Export("location")]		public CGFloat Location { get; private set; }				/// <summary>		/// The aligment of tab column contents.		/// </summary>		[iOSVersion(7)]		[Export("alignment")]		public NSTextAlignment Alignment { get; private set; }				/// <summary>		/// This constant specifies the terminating character for a tab column.		/// </summary>		[Export("NSTabColumnTerminatorsAttributeName")]		public string NSTabColumnTerminatorsAttributeName { get; private set; }	}}