using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIAccelerometerDelegate_Protocol/index.html#//apple_ref/occ/intf/UIAccelerometerDelegate	/// <summary>	/// The UIAccelerometerDelegate protocol defines a single method for receiving acceleration-related data from the system. Implementation of this method is optional, but expected.	/// </summary>	[iOSVersion(2)]	public interface UIAccelerometerDelegate	{		/// <summary>		/// Delivers the latest acceleration data to the delegate.		/// </summary>		/// <param name="accelerometer">The application-wide accelerometer object.</param>		/// <param name="didAccelerate">The most recent acceleration data.</param>		[iOSVersion(2)]		[Export("accelerometer")]		[Optional]		void Accelerometer(UIAccelerometer accelerometer, UIAcceleration didAccelerate);	}}