using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIPickerViewAccessibilityDelegate_Protocol/index.html#//apple_ref/occ/intf/UIPickerViewAccessibilityDelegate	/// <summary>	/// The UIPickerViewAccessibilityDelegate protocol defines methods you can implement to provide accessibility information for individual components of a picker view.	/// </summary>	[iOSVersion(4)]	public interface UIPickerViewAccessibilityDelegate	{		/// <summary>		/// Returns a label that identifies the picker view component.		/// </summary>		/// <returns>A succinct label, in a localized string, that identifies the picker view component.</returns>		[iOSVersion(4)]		[Export("pickerView")]		[Optional]		string PickerView(UIPickerView pickerView, int accessibilityLabelForComponent);				/// <summary>		/// Returns a hint that describes the result of performing an action on the picker view component.		/// </summary>		/// <returns>A brief description, in a localized string, of the result of performing an action on the picker view component.</returns>		[iOSVersion(4)]		[Export("pickerView")]		[Optional]		string PickerView(UIPickerView pickerView, int accessibilityHintForComponent);	}}