using CoreGraphics;
using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIAccessibilityReadingContent_Protocol/index.html#//apple_ref/occ/intf/UIAccessibilityReadingContent	/// <summary>	/// The UIAccessibilityReadingContent protocol can be implemented on an object that represents content that is intended to be read by users, such as a book or an article. To give VoiceOver users a superior, continuous reading experience, you can implement this protocol on such an element, characterize it with the UIAccessibilityTraitCausesPageTurn trait, and use the UIAccessibilityScrollDirectionNext and UIAccessibilityScrollDirectionPrevious constants to enable page turning.	/// </summary>	[iOSVersion(5)]	public interface UIAccessibilityReadingContent	{		/// <summary>		/// Returns the line number that contains the specified point. (required)		/// </summary>		/// <param name="point">A point within the bounds of the receiver’s view space, in screen coordinates. That is, a point for which [self pointInside:point withEvent:event] == YES.</param>		/// <returns>The line number that contains the specified point or NSNotFound if the point indicates an empty area within the receiver’s rectangle. By default, this method returns NSNotFound.</returns>		[iOSVersion(5)]		[Export("accessibilityLineNumberForPoint")]		int AccessibilityLineNumberForPoint(CGPoint point);				/// <summary>		/// Returns the text associated with the specified line number. (required)		/// </summary>		/// <param name="lineNumber">A line number in the receiver’s content.</param>		/// <returns>A string containing the text that is associated with the specified line number, or nil if the line number is invalid. By default, this function returns nil.</returns>		[iOSVersion(5)]		[Export("accessibilityContentForLineNumber")]		string AccessibilityContentForLineNumber(int lineNumber);				/// <summary>		/// Returns the onscreen frame associated with the specified line number. (required)		/// </summary>		/// <param name="lineNumber">The line number.</param>		/// <returns>The frame in the receiver that contains the specified line number, in screen coordinates. By default, this method returns CGRectZero.</returns>		[iOSVersion(5)]		[Export("accessibilityFrameForLineNumber")]		CGRect AccessibilityFrameForLineNumber(int lineNumber);				/// <summary>		/// Returns the text displayed on the current page. (required)		/// </summary>		/// <returns>A string that contains the text displayed on the current page.</returns>		[iOSVersion(5)]		[Export("accessibilityPageContent")]		string AccessibilityPageContent();	}}