using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIModalViewDelegate_Protocol/index.html#//apple_ref/occ/intf/UIActionSheetDelegate	/// <summary>	/// The UIActionSheetDelegate protocol defines the methods a delegate of a UIActionSheet object should implement. The delegate implements the button actions and any other custom behavior. Some of the methods defined in this protocol are optional.	/// </summary>	[iOSVersion(2)]	public interface UIActionSheetDelegate	{		/// <summary>		/// Sent to the delegate when the user clicks a button on an action sheet.		/// </summary>		/// <param name="actionSheet">The action sheet containing the button.</param>		/// <param name="clickedButtonAtIndex">The position of the clicked button. The button indices start at 0.</param>		[iOSVersion(2)]		[Export("actionSheet")]		[Optional]		void ActionSheet(UIActionSheet actionSheet, int clickedButtonAtIndex);				/// <summary>		/// Sent to the delegate before an action sheet is presented to the user.		/// </summary>		/// <param name="actionSheet">The action sheet that is about to be displayed.</param>		[iOSVersion(2)]		[Export("willPresentActionSheet")]		[Optional]		void WillPresentActionSheet(UIActionSheet actionSheet);				/// <summary>		/// Sent to the delegate after an action sheet is presented to the user.		/// </summary>		/// <param name="actionSheet">The action sheet that was displayed.</param>		[iOSVersion(2)]		[Export("didPresentActionSheet")]		[Optional]		void DidPresentActionSheet(UIActionSheet actionSheet);				/// <summary>		/// Sent to the delegate before an action sheet is dismissed.		/// </summary>		/// <param name="actionSheet">The action sheet that is about to be dismissed.</param>		/// <param name="willDismissWithButtonIndex">The index of the button that was clicked. If this is the cancel button index, the action sheet is canceling. If -1, the cancel button index is not set.</param>		[iOSVersion(2)]		[Export("actionSheet")]		[Optional]		void ActionSheet(UIActionSheet actionSheet, int willDismissWithButtonIndex);				/// <summary>		/// Sent to the delegate after an action sheet is dismissed from the screen.		/// </summary>		/// <param name="actionSheet">The action sheet that was dismissed.</param>		/// <param name="didDismissWithButtonIndex">The index of the button that was clicked. The button indices start at 0. If this is the cancel button index, the action sheet is canceling. If -1, the cancel button index is not set.</param>		[iOSVersion(2)]		[Export("actionSheet")]		[Optional]		void ActionSheet(UIActionSheet actionSheet, int didDismissWithButtonIndex);				/// <summary>		/// Sent to the delegate before an action sheet is canceled.		/// </summary>		/// <param name="actionSheet">The action sheet that will be canceled.</param>		[iOSVersion(2)]		[Export("actionSheetCancel")]		[Optional]		void ActionSheetCancel(UIActionSheet actionSheet);	}}