using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIDynamicItem_Protocol/index.html#//apple_ref/occ/intf/UIDynamicItem	/// <summary>	/// To make a custom object eligible to participate in UIKit Dynamics, adopt the UIDynamicItem protocol in the objectâ€™s class.	/// </summary>	[iOSVersion(7)]	public interface UIDynamicItem	{		/// <summary>		/// Called when a dynamic animator needs the bounds of the dynamic item. (required) (read-only)		/// </summary>		[iOSVersion(7)]		[Export("bounds")]		CGRect Bounds;				/// <summary>		/// The center point of the dynamic item. (required)		/// </summary>		[iOSVersion(7)]		[Export("center")]		CGPoint Center;				/// <summary>		/// The rotation of the dynamic item. (required)		/// </summary>		[iOSVersion(7)]		[Export("transform")]		CGAffineTransform Transform;	}}