using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIKeyInput_Protocol/index.html#//apple_ref/occ/intf/UIKeyInput	/// <summary>	/// A subclass of UIResponder can adopt this protocol to implement simple text entry. When instances of this subclass are the first responder, the system keyboard is displayed.	/// </summary>	[iOSVersion(3.2)]	public interface UIKeyInput	{		/// <summary>		/// Insert a character into the displayed text. (required)		/// </summary>		/// <param name="text">A string object representing the character typed on the system keyboard.</param>		[iOSVersion(3.2)]		[Export("insertText")]		void InsertText(string text);				/// <summary>		/// Delete a character from the displayed text. (required)		/// </summary>		[iOSVersion(3.2)]		[Export("deleteBackward")]		void DeleteBackward();				/// <summary>		/// A Boolean value that indicates whether the text-entry objects has any text. (required)		/// </summary>		/// <returns>true if the backing store has textual content, false otherwise.</returns>		[iOSVersion(3.2)]		[Export("hasText")]		bool HasText();	}}