using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIPickerViewDelegate_Protocol/index.html#//apple_ref/occ/intf/UIPickerViewDelegate	/// <summary>	/// The delegate of a UIPickerView object must adopt this protocol and implement at least some of its methods to provide the picker view with the data it needs to construct itself.	/// </summary>	[iOSVersion(2)]	public interface UIPickerViewDelegate	{		/// <summary>		/// Called by the picker view when it needs the row height to use for drawing row content.		/// </summary>		/// <param name="pickerView">The picker view requesting this information.</param>		/// <param name="rowHeightForComponent">A zero-indexed number identifying a component of pickerView. Components are numbered left-to-right.</param>		/// <returns>A float value indicating the height of the row in points.</returns>		[iOSVersion(2)]		[Export("pickerView")]		[Optional]		CGFloat PickerView(UIPickerView pickerView, int rowHeightForComponent);				/// <summary>		/// Called by the picker view when it needs the row width to use for drawing row content.		/// </summary>		/// <param name="pickerView">The picker view requesting this information.</param>		/// <param name="widthForComponent">A zero-indexed number identifying a component of the picker view. Components are numbered left-to-right.</param>		/// <returns>A float value indicating the width of the row in points.</returns>		[iOSVersion(2)]		[Export("pickerView")]		[Optional]		CGFloat PickerView(UIPickerView pickerView, int widthForComponent);				/// <summary>		/// Called by the picker view when it needs the title to use for a given row in a given component.		/// </summary>		/// <param name="pickerView">An object representing the picker view requesting the data.</param>		/// <param name="titleForRow">A zero-indexed number identifying a row of component. Rows are numbered top-to-bottom.</param>		/// <param name="forComponent">A zero-indexed number identifying a component of pickerView. Components are numbered left-to-right.</param>		/// <returns>The string to use as the title of the indicated component row.</returns>		[iOSVersion(2)]		[Export("pickerView")]		[Optional]		string PickerView(UIPickerView pickerView, int titleForRow, int forComponent);				/// <summary>		/// Called by the picker view when it needs the styled title to use for a given row in a given component.		/// </summary>		/// <param name="pickerView">An object representing the picker view requesting the data.</param>		/// <param name="attributedTitleForRow">A zero-indexed number identifying a row of component. Rows are numbered top-to-bottom.</param>		/// <param name="forComponent">A zero-indexed number identifying a component of pickerView. Components are numbered left-to-right.</param>		/// <returns>The attributed string to use as the title of the indicated component row.</returns>		[iOSVersion(6)]		[Export("pickerView")]		[Optional]		NSAttributedString PickerView(UIPickerView pickerView, int attributedTitleForRow, int forComponent);				/// <summary>		/// Called by the picker view when it needs the view to use for a given row in a given component.		/// </summary>		/// <param name="pickerView">An object representing the picker view requesting the data.</param>		/// <param name="viewForRow">A zero-indexed number identifying a row of component. Rows are numbered top-to-bottom.</param>		/// <param name="forComponent">A zero-indexed number identifying a component of pickerView. Components are numbered left-to-right.</param>		/// <param name="reusingView">A view object that was previously used for this row, but is now hidden and cached by the picker view.</param>		/// <returns>A view object to use as the content of row. The object can be any subclass of UIView, such as UILabel, UIImageView, or even a custom view.</returns>		[iOSVersion(2)]		[Export("pickerView")]		[Optional]		UIView PickerView(UIPickerView pickerView, int viewForRow, int forComponent, UIView reusingView);				/// <summary>		/// Called by the picker view when the user selects a row in a component.		/// </summary>		/// <param name="pickerView">An object representing the picker view requesting the data.</param>		/// <param name="didSelectRow">A zero-indexed number identifying a row of component. Rows are numbered top-to-bottom.</param>		/// <param name="inComponent">A zero-indexed number identifying a component of pickerView. Components are numbered left-to-right.</param>		[iOSVersion(2)]		[Export("pickerView")]		[Optional]		void PickerView(UIPickerView pickerView, int didSelectRow, int inComponent);	}}