using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIScrollViewAccessibilityDelegate_Protocol/index.html#//apple_ref/occ/intf/UIScrollViewAccessibilityDelegate	/// <summary>	/// The UIScrollViewAccessibilityDelegate protocol defines methods you can implement to provide accessibility information for a scrollview.	/// </summary>	[iOSVersion(6)]	public interface UIScrollViewAccessibilityDelegate	{		/// <summary>		/// Returns a custom status string for the current offset.		/// </summary>		/// <returns>A custom status string for the current offset.</returns>		[iOSVersion(6)]		[Export("accessibilityScrollStatusForScrollView")]		[Optional]		string AccessibilityScrollStatusForScrollView(UIScrollView scrollView);	}}