using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// Use the UISearchResultsUpdating protocol to update search results based on information the user enters into the search bar.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UISearchResultsUpdating_ClassRef/index.html#//apple_ref/occ/intf/UISearchResultsUpdating"/>    [iOSVersion(8)]    public interface UISearchResultsUpdating//: NSObjectProtocol    {        /// <summary>        /// Called when the search bar becomes the first responder or when the user makes changes inside the search bar. (required)        /// </summary>        /// <param name="searchController">The UISearchController object used as the search bar.</param>        [iOSVersion(8)]        [Export("updateSearchResultsForSearchController")]        void UpdateSearchResultsForSearchController(UISearchController searchController);    }}