using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITextDocumentProxy_Protocol/index.html#//apple_ref/occ/intf/UITextDocumentProxy	/// <summary>	/// A text document proxy provides textual context to a custom keyboard (which is based on the UIInputViewController class) by way of the keyboardâ€™s textDocumentProxy property.	/// </summary>	[iOSVersion(8)]	public interface UITextDocumentProxy	{		/// <summary>		/// Textual context after the insertion point in the current text input object. (required) (read-only)		/// </summary>		[iOSVersion(8)]		[Export("documentContextAfterInput")]		string DocumentContextAfterInput;				/// <summary>		/// Textual context before the insertion point in the current text input object. (required) (read-only)		/// </summary>		[iOSVersion(8)]		[Export("documentContextBeforeInput")]		string DocumentContextBeforeInput;	}}