using ObjectiveC;using System;using SwiftSharp.Attributes;using System.Collections.Generic;namespace UIKit{    /// <summary>    /// The UITextInputTraits protocol defines features that are associated with keyboard input. All objects that support keyboard input must adopt this protocol in order to interact properly with the text input management system. The UITextField and UITextView classes already support this protocol.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITextInputTraits_Protocol/index.html#//apple_ref/occ/intf/UITextInputTraits"/>    [iOSVersion(2)]    public interface UITextInputTraits//: NSObjectProtocol    {        /// <summary>        /// The auto-capitalization style for the text object.        /// </summary>        [iOSVersion(2)]        [Export("autocapitalizationType")]        UITextAutocapitalizationType AutocapitalizationType { get; set; }                /// <summary>        /// The auto-correction style for the text object.        /// </summary>        [iOSVersion(2)]        [Export("autocorrectionType")]        UITextAutocorrectionType AutocorrectionType { get; set; }                /// <summary>        /// The spell-checking style for the text object.        /// </summary>        [iOSVersion(5)]        [Export("spellCheckingType")]        UITextSpellCheckingType SpellCheckingType { get; set; }                /// <summary>        /// A Boolean value indicating whether the return key is automatically enabled when text is entered by the user.        /// </summary>        [iOSVersion(2)]        [Export("enablesReturnKeyAutomatically")]        bool EnablesReturnKeyAutomatically { get; set; }                /// <summary>        /// The appearance style of the keyboard that is associated with the text object        /// </summary>        [iOSVersion(2)]        [Export("keyboardAppearance")]        UIKeyboardAppearance KeyboardAppearance { get; set; }                /// <summary>        /// The keyboard style associated with the text object.        /// </summary>        [iOSVersion(2)]        [Export("keyboardType")]        UIKeyboardType KeyboardType { get; set; }                /// <summary>        /// The contents of the “return” key.        /// </summary>        [iOSVersion(2)]        [Export("returnKeyType")]        UIReturnKeyType ReturnKeyType { get; set; }                /// <summary>        /// Identifies whether the text object should hide the text being entered.        /// </summary>        [iOSVersion(2)]        [Export("secureTextEntry")]        bool SecureTextEntry { get; set; }    }    /// <summary>    /// The auto-capitalization behavior of a text-based view.    /// </summary>    [iOSVersion(2)]    public enum UITextAutocapitalizationType    {        /// <summary>        /// Do not capitalize any text automatically.        /// </summary>        [iOSVersion(2)]        None,        /// <summary>        /// Capitalize the first letter of each word automatically.        /// </summary>        [iOSVersion(2)]        Words,        /// <summary>        /// Capitalize the first letter of each sentence automatically.        /// </summary>        [iOSVersion(2)]        Sentences,        /// <summary>        /// Capitalize all characters automatically.        /// </summary>        [iOSVersion(2)]        AllCharacters,    }    /// <summary>    /// The auto-correction behavior of a text-based view.    /// </summary>    [iOSVersion(2)]    public enum UITextAutocorrectionType    {        /// <summary>        /// Choose an appropriate auto-correction behavior for the current script system.        /// </summary>        [iOSVersion(2)]        Default,        /// <summary>        /// Disable auto-correction behavior.        /// </summary>        [iOSVersion(2)]        No,        /// <summary>        /// Enable auto-correction behavior.        /// </summary>        [iOSVersion(2)]        Yes,    }    /// <summary>    /// The spell-checking behavior of a text-based view.    /// </summary>    [iOSVersion(5)]    public enum UITextSpellCheckingType    {        /// <summary>        /// Use the default spell-checking behavior. The default behavior is to enable spell-checking whenever auto-correction is also enabled.        /// </summary>        [iOSVersion(5)]        Default,        /// <summary>        /// Disable spell-checking behavior.        /// </summary>        [iOSVersion(5)]        No,        /// <summary>        /// Enable spell-checking behavior.        /// </summary>        [iOSVersion(5)]        Yes,    }    /// <summary>    /// The type of keyboard to display for a given text-based view.    /// </summary>    [iOSVersion(2)]    public enum UIKeyboardType    {        /// <summary>        /// Use the default keyboard for the current input method.        /// </summary>        [iOSVersion(2)]        Default,        /// <summary>        /// Use a keyboard that displays standard ASCII characters.        /// </summary>        [iOSVersion(2)]        ASCIICapable,        /// <summary>        /// Use the numbers and punctuation keyboard.        /// </summary>        [iOSVersion(2)]        NumbersAndPunctuation,        /// <summary>        /// Use a keyboard optimized for URL entry. This type features “.”, “/”, and “.com” prominently.        /// </summary>        [iOSVersion(2)]        URL,        /// <summary>        /// Use a numeric keypad designed for PIN entry. This type features the numbers 0 through 9 prominently. This keyboard type does not support auto-capitalization.        /// </summary>        [iOSVersion(2)]        NumberPad,        /// <summary>        /// Use a keypad designed for entering telephone numbers. This type features the numbers 0 through 9 and the “*” and “#” characters prominently. This keyboard type does not support auto-capitalization.        /// </summary>        [iOSVersion(2)]        PhonePad,        /// <summary>        /// Use a keypad designed for entering a person’s name or phone number. This keyboard type does not support auto-capitalization.        /// </summary>        [iOSVersion(2)]        NamePhonePad,        /// <summary>        /// Use a keyboard optimized for specifying email addresses. This type features the “@”, “.” and space characters prominently.        /// </summary>        [iOSVersion(2)]        EmailAddress,        /// <summary>        /// Use a keyboard with numbers and a decimal point.        /// </summary>        [iOSVersion(4.1)]        DecimalPad,        /// <summary>        /// Use a keyboard optimized for twitter text entry, with easy access to the @ and # characters.        /// </summary>        [iOSVersion(5)]        Twitter,        /// <summary>        /// Use a keyboard optimized for web search terms and URL entry. This type features the space and “.” characters prominently.        /// </summary>        [iOSVersion(7)]        WebSearch,    }    /// <summary>    /// The appearance of the keyboard used by a text-based view.    /// </summary>    [iOSVersion(2)]    public enum UIKeyboardAppearance    {        /// <summary>        /// Use the default keyboard appearance for the current input method. This constant corresponds to the UIKeyboardAppearanceLight value.        /// </summary>        [iOSVersion(2)]        Default,        /// <summary>        /// Use a keyboard where a dark look is required.        /// </summary>        [iOSVersion(7)]        Dark,        /// <summary>        /// Use a keyboard where a light look is required.        /// </summary>        [iOSVersion(7)]        Light,    }    /// <summary>    /// The text string displayed in the “return” key of a keyboard.    /// </summary>    [iOSVersion(2)]    public enum UIReturnKeyType    {        /// <summary>        /// Set the text of the return key to “return”.        /// </summary>        [iOSVersion(2)]        Default,        /// <summary>        /// Set the text of the return key to “Go”.        /// </summary>        [iOSVersion(2)]        Go,        /// <summary>        /// Set the text of the return key to “Google”.        /// </summary>        [iOSVersion(2)]        Google,        /// <summary>        /// Set the text of the return key to “Join”.        /// </summary>        [iOSVersion(2)]        Join,        /// <summary>        /// Set the text of the return key to “Next”.        /// </summary>        [iOSVersion(2)]        Next,        /// <summary>        /// Set the text of the return key to “Route”.        /// </summary>        [iOSVersion(2)]        Route,        /// <summary>        /// Set the text of the return key to “Search”.        /// </summary>        [iOSVersion(2)]        Search,        /// <summary>        /// Set the text of the return key to “Send”.        /// </summary>        [iOSVersion(2)]        Send,        /// <summary>        /// Set the text of the return key to “Yahoo”.        /// </summary>        [iOSVersion(2)]        Yahoo,        /// <summary>        /// Set the text of the return key to “Done”.        /// </summary>        [iOSVersion(2)]        Done,        /// <summary>        /// Set the text of the return key to “Emergency Call”.        /// </summary>        [iOSVersion(2)]        EmergencyCall,    }}