using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITraitEnvironment_Ref/index.html#//apple_ref/occ/intf/UITraitEnvironment	/// <summary>	/// The UITraitEnvironment protocol provides access to a view collectionâ€™s traits. The traitCollectionDidChange: method provides you with a way to respond to changes in the trait collection.	/// </summary>	[iOSVersion(8)]	public interface UITraitEnvironment	{		/// <summary>		/// The collection of traits for the view. (required) (read-only)		/// </summary>		[iOSVersion(8)]		[Export("traitCollection")]		UITraitCollection TraitCollection;				/// <summary>		/// Provides a way to perform custom behavior when the current trait collection changes. (required)		/// </summary>		/// <param name="previousTraitCollection">The trait collection before any traits were changed.</param>		[iOSVersion(8)]		[Export("traitCollectionDidChange")]		void TraitCollectionDidChange(UITraitCollection previousTraitCollection);	}}