using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// A UIAccessibilityCustomAction object represents a custom action to be performed on an accessible object. Apps that support custom actions can create instances of this class, specifying the user-readable name of the action and the object and selector to use when performing the action. Assistive technologies display custom actions in response to specific user cues. For example, VoiceOver lets users access actions quickly using the Actions rotor.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIAccessibilityCustomAction_class/index.html#//apple_ref/occ/cl/UIAccessibilityCustomAction"/>    [iOSVersion(8)]    [Export("")]    public class UIAccessibilityCustomAction : NSObject//, AnyObject, CVarArgType, Equatable, Hashable, NSObjectProtocol, Printable    {        public UIAccessibilityCustomAction() { }        /// <summary>        /// Returns an initialized custom action object with the specified name.        /// </summary>        /// <param name="name">The localized name of the action. Provide a short and descriptive name for the action.</param>        /// <param name="target">The object that performs the action.</param>        /// <param name="selector">The selector of target to call when you want to perform the action. The method signature must take one of the following forms:         ///           - (BOOL)myPerformActionMethod        ///           - (BOOL)myPerformActionMethod:(UIAccessibilityCustomAction *)action</param>        [iOSVersion(8)]        public UIAccessibilityCustomAction([Unwrapped] string name, [Unwrapped] AnyObject target, Selector selector) { }                /// <summary>        /// The localized name of the action.        /// </summary>        [iOSVersion(8)]        [Export("name")]        [Unwrapped]        public string Name { get; set; }                /// <summary>        /// The object that performs the action.        /// </summary>        [iOSVersion(8)]        [Export("target")]        [Optional]        public AnyObject Target { get; set; }                /// <summary>        /// The method that performs the action.        /// </summary>        [iOSVersion(8)]        [Export("selector")]        public Selector Selector { get; set; }    }}