using CoreGraphics;
using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIAccessibilityElement_Class/index.html#//apple_ref/occ/cl/UIAccessibilityElement	/// <summary>	/// The UIAccessibilityElement class encapsulates information about an item that should be accessible to users with disabilities, but that isnâ€™t accessible by default. For example, an icon or text image is not automatically accessible because it does not inherit from UIView (or UIControl). A view that contains such nonview items creates an instance of UIAccessibilityElement to represent each item that needs to be accessible.	/// </summary>	[iOSVersion(3)]	public class UIAccessibilityElement : NSObject	{		/// <summary>		/// Creates and initializes an accessibility element to represent an item in the specified container.		/// </summary>		/// <param name="accessibilityContainer">The view that contains the item.</param>		/// <returns>An accessibility element to represent a non-view item in the container.</returns>		[iOSVersion(3)]		public UIAccessibilityElement(AnyObject accessibilityContainer) { }				/// <summary>		/// The view that contains the accessibility element.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityContainer")]		public AnyObject AccessibilityContainer { get; set; }				/// <summary>		/// A Boolean value indicating whether the item is an accessibility element an assistive application can access.		/// </summary>		[iOSVersion(3)]		[Export("isAccessibilityElement")]		public bool IsAccessibilityElement { get; set; }				/// <summary>		/// A string that succinctly identifies the accessibility element.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityLabel")]		public string AccessibilityLabel { get; set; }				/// <summary>		/// A string that briefly describes the result of performing an action on the accessibility element.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityHint")]		public string AccessibilityHint { get; set; }				/// <summary>		/// A string that represents the current value of the accessibility element.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityValue")]		public string AccessibilityValue { get; set; }				/// <summary>		/// The frame of the accessibility element, in screen coordinates.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityFrame")]		public CGRect AccessibilityFrame { get; set; }				/// <summary>		/// The combination of traits that best characterize the accessibility element.		/// </summary>		[iOSVersion(3)]		[Export("accessibilityTraits")]		public UIAccessibilityTraits AccessibilityTraits { get; set; }	}}