using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// Use an activity indicator to show that a task is in progress. An activity indicator appears as a “gear” that is either spinning or stopped.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIActivityIndicatorView_Class/index.html#//apple_ref/occ/cl/UIActivityIndicatorView"/>    [iOSVersion(2)]    [Export("")]    public class UIActivityIndicatorView : UIView//, AnyObject, CVarArgType, Equatable, Hashable, NSCoding, NSObjectProtocol, Printable, Reflectable, UIAccessibilityIdentification, UIAppearance, UIAppearanceContainer, UICoordinateSpace, UIDynamicItem, UITraitEnvironment    {        public UIActivityIndicatorView() { }        /// <summary>        /// Starts the animation of the progress indicator.        /// </summary>        [iOSVersion(2)]        [Export("startAnimating")]        public virtual void StartAnimating() {  }                /// <summary>        /// Stops the animation of the progress indicator.        /// </summary>        [iOSVersion(2)]        [Export("stopAnimating")]        public virtual void StopAnimating() {  }                /// <summary>        /// Returns whether the receiver is animating.        /// </summary>        [iOSVersion(2)]        [Export("isAnimating")]        public virtual bool IsAnimating() { return default(bool); }                /// <summary>        /// A Boolean value that controls whether the receiver is hidden when the animation is stopped.        /// </summary>        [iOSVersion(2)]        [Export("hidesWhenStopped")]        public bool HidesWhenStopped { get; set; }                /// <summary>        /// The basic appearance of the activity indicator.        /// </summary>        [iOSVersion(2)]        [Export("activityIndicatorViewStyle")]        public UIActivityIndicatorViewStyle ActivityIndicatorViewStyle { get; set; }                /// <summary>        /// The color of the activity indicator.        /// </summary>        [iOSVersion(5)]        [Export("color")]        [Unwrapped]        public UIColor Color { get; set; }    }    /// <summary>    /// The visual style of the progress indicator.    /// </summary>    public enum UIActivityIndicatorViewStyle    {        /// <summary>        /// The large white style of indicator.        /// </summary>        [iOSVersion(2)]        WhiteLarge,        /// <summary>        /// The standard white style of indicator (the default).        /// </summary>        [iOSVersion(2)]        White,        /// <summary>        /// The standard gray style of indicator.        /// </summary>        [iOSVersion(2)]        Gray,    }}