using ObjectiveC;using System;using SwiftSharp.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// UIPanGestureRecognizer is a concrete subclass of UIGestureRecognizer that looks for panning (dragging) gestures. The user must be pressing one or more fingers on a view while they pan it. Clients implementing the action method for this gesture recognizer can ask it for the current translation and velocity of the gesture.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIPanGestureRecognizer_Class/index.html#//apple_ref/occ/cl/UIPanGestureRecognizer"/>    [iOSVersion(3.2)]    public class UIPanGestureRecognizer : UIGestureRecognizer//, AnyObject, NSObjectProtocol    {        /// <summary>        /// The translation of the pan gesture in the coordinate system of the specified view.        /// </summary>        /// <param name="view">The view in whose coordinate system the translation of the pan gesture should be computed. If you want to adjust a view's location to keep it under the user's finger, request the translation in that view's superview's coordinate system.</param>        [iOSVersion(3.2)]        [Export("translationInView")]        public CGPoint TranslationInView(UIView view) { return default(CGPoint); }                /// <summary>        /// Sets the translation value in the coordinate system of the specified view.        /// </summary>        /// <param name="translation">A point that identifies the new translation value.</param>        /// <param name="inView">A view in whose coordinate system the translation is to occur.</param>        [iOSVersion(3.2)]        [Export("setTranslation")]        public void SetTranslation(CGPoint translation, UIView inView) {  }                /// <summary>        /// The velocity of the pan gesture in the coordinate system of the specified view.        /// </summary>        /// <param name="view">The view in whose coordinate system the velocity of the pan gesture is computed.</param>        [iOSVersion(3.2)]        [Export("velocityInView")]        public CGPoint VelocityInView(UIView view) { return default(CGPoint); }                /// <summary>        /// The maximum number of fingers that can be touching the view for this gesture to be recognized.        /// </summary>        [iOSVersion(3.2)]        [Export("maximumNumberOfTouches")]        public int MaximumNumberOfTouches { get; set; }                /// <summary>        /// The minimum number of fingers that can be touching the view for this gesture to be recognized.        /// </summary>        [iOSVersion(3.2)]        [Export("minimumNumberOfTouches")]        public int MinimumNumberOfTouches { get; set; }    }}