using CoreGraphics;
using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIPopoverBackgroundView_class/index.html#//apple_ref/occ/cl/UIPopoverBackgroundView	/// <summary>	/// The UIPopoverBackgroundView class provides the background appearance for a popover. This class must be subclassed before it can be used. The implementation of your subclass is responsible for providing the border decoration and arrow for the popover. Subclasses must override all declared properties and methods to provide information about where to lay out the corresponding popover content and arrow. Subclasses must also provide implementations for all methods of the UIPopoverBackgroundViewMethods protocol.	/// </summary>	[iOSVersion(5)]	public class UIPopoverBackgroundView : UIView	{		/// <summary>		/// The distance (measured in points) from the center of the view to the center line of the arrow.		/// </summary>		[iOSVersion(5)]		[Export("arrowOffset")]		public CGFloat ArrowOffset { get; set; }				/// <summary>		/// The direction in which the popover arrow is pointing.		/// </summary>		[iOSVersion(5)]		[Export("arrowDirection")]		public UIPopoverArrowDirection ArrowDirection { get; set; }				/// <summary>		/// Determines whether the default content appearance should be used for the popover.		/// </summary>		[iOSVersion(6)]		[Export("wantsDefaultContentAppearance")]		public static bool WantsDefaultContentAppearance() { return false; }	}}