using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIRefreshControl_class/index.html#//apple_ref/occ/cl/UIRefreshControl	/// <summary>	/// A UIRefreshControl object provides a standard control that can be used to initiate the refreshing of a table view’s contents. You link a refresh control to a table through an associated table view controller object. The table view controller handles the work of adding the control to the table’s visual appearance and managing the display of that control in response to appropriate user gestures.	/// </summary>	[iOSVersion(6)]	public class UIRefreshControl : UIControl	{		/// <summary>		/// Initializes and returns a standard refresh control.		/// </summary>		/// <returns>An initialized refresh control.</returns>		[iOSVersion(6)]		public UIRefreshControl() { }				/// <summary>		/// The tint color for the refresh control.		/// </summary>		[iOSVersion(6)]		[Export("tintColor")]		public UIColor TintColor { get; set; }				/// <summary>		/// The styled title text to display in the refresh control.		/// </summary>		[iOSVersion(6)]		[Export("attributedTitle")]		public NSAttributedString AttributedTitle { get; set; }				/// <summary>		/// Tells the control that a refresh operation was started programmatically.		/// </summary>		[iOSVersion(6)]		[Export("beginRefreshing")]		public void BeginRefreshing() { }				/// <summary>		/// Tells the control that a refresh operation has ended.		/// </summary>		[iOSVersion(6)]		[Export("endRefreshing")]		public void EndRefreshing() { }				/// <summary>		/// A Boolean value indicating whether a refresh operation has been triggered and is in progress. (read-only)		/// </summary>		[iOSVersion(6)]		[Export("refreshing")]		public bool Refreshing { get; private set; }	}}