using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// A UIRefreshControl object provides a standard control that can be used to initiate the refreshing of a table view’s contents. You link a refresh control to a table through an associated table view controller object. The table view controller handles the work of adding the control to the table’s visual appearance and managing the display of that control in response to appropriate user gestures.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIRefreshControl_class/index.html#//apple_ref/occ/cl/UIRefreshControl"/>    [iOSVersion(6)]    public class UIRefreshControl : UIControl//, AnyObject, NSCoding, NSObjectProtocol, Reflectable, UIAccessibilityIdentification, UIAppearance, UIAppearanceContainer, UICoordinateSpace, UIDynamicItem, UITraitEnvironment    {        /// <summary>        /// Initializes and returns a standard refresh control.        /// </summary>        [iOSVersion(6)]        public UIRefreshControl() { }                /// <summary>        /// Tells the control that a refresh operation was started programmatically.        /// </summary>        [iOSVersion(6)]        [Export("beginRefreshing")]        public void BeginRefreshing() {  }                /// <summary>        /// Tells the control that a refresh operation has ended.        /// </summary>        [iOSVersion(6)]        [Export("endRefreshing")]        public void EndRefreshing() {  }                /// <summary>        /// The tint color for the refresh control.        /// </summary>        [iOSVersion(6)]        [Export("tintColor")]        public UIColor TintColor { get; set; }                /// <summary>        /// The styled title text to display in the refresh control.        /// </summary>        [iOSVersion(6)]        [Export("attributedTitle")]        public NSAttributedString AttributedTitle { get; set; }                /// <summary>        /// A Boolean value indicating whether a refresh operation has been triggered and is in progress. (read-only)        /// </summary>        [iOSVersion(6)]        [Export("refreshing")]        public bool Refreshing { get; private set; }    }}