using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// A UIScreenMode object represents a possible set of attributes that can be applied to a UIScreen object. The object encapsulates information about the size of the screenâ€™s underlying display buffer and the aspect ratio it uses for individual pixels.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIScreenMode_class/index.html#//apple_ref/occ/cl/UIScreenMode"/>    [iOSVersion(3.2)]    public class UIScreenMode : NSObject//, AnyObject, NSObjectProtocol    {        public UIScreenMode() { }        /// <summary>        /// The screen size, measured in pixels. (read-only)        /// </summary>        [iOSVersion(3.2)]        [Export("size")]        public CGSize Size { get; private set; }                /// <summary>        /// The aspect ratio of a single pixel. (read-only)        /// </summary>        [iOSVersion(3.2)]        [Export("pixelAspectRatio")]        public CGFloat PixelAspectRatio { get; private set; }    }}