using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UIScreenMode_class/index.html#//apple_ref/occ/cl/UIScreenMode	/// <summary>	/// A UIScreenMode object represents a possible set of attributes that can be applied to a UIScreen object. The object encapsulates information about the size of the screenâ€™s underlying display buffer and the aspect ratio it uses for individual pixels.	/// </summary>	[iOSVersion(3.2)]	public class UIScreenMode : NSObject	{		/// <summary>		/// The screen size, measured in pixels. (read-only)		/// </summary>		[iOSVersion(3.2)]		[Export("size")]		public CGSize Size { get; private set; }				/// <summary>		/// The aspect ratio of a single pixel. (read-only)		/// </summary>		[iOSVersion(3.2)]		[Export("pixelAspectRatio")]		public CGFloat PixelAspectRatio { get; private set; }	}}