using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UISearchController/index.html#//apple_ref/occ/cl/UISearchController	/// <summary>	/// A search controller manages the presentation of a search bar (in concert with the results view controller’s content).	/// </summary>	[iOSVersion(8)]	public class UISearchController : UIViewController	{		/// <summary>		/// Returns a new search controller initialized with the given search results controller.		/// </summary>		/// <param name="searchResultsController">The controller that manages the search results.</param>		/// <returns>A search controller initialized with the given search results controller.</returns>		[iOSVersion(8)]		public UISearchController(UIViewController searchResultsController) : base("", null) { }				/// <summary>		/// The presented state of the search interface.		/// </summary>		[iOSVersion(8)]		[Export("active")]		public bool Active { get; set; }				/// <summary>		/// The controller’s delegate.		/// </summary>		[iOSVersion(8)]		[Export("delegate")]		public UISearchControllerDelegate Delegate { get; set; }				/// <summary>		/// Determines if the underlying content is dimmed while searching.		/// </summary>		[iOSVersion(8)]		[Export("dimsBackgroundDuringPresentation")]		public bool DimsBackgroundDuringPresentation { get; set; }				/// <summary>		/// Determines if the navigation bar is hidden during searching.		/// </summary>		[iOSVersion(8)]		[Export("hidesNavigationBarDuringPresentation")]		public bool HidesNavigationBarDuringPresentation { get; set; }				/// <summary>		/// The search bar. (read-only)		/// </summary>		[iOSVersion(8)]		[Export("searchBar")]		public UISearchBar SearchBar { get; private set; }				/// <summary>		/// The view controller that manages the results of the search. (read-only)		/// </summary>		[iOSVersion(8)]		[Export("searchResultsController")]		public UIViewController SearchResultsController { get; private set; }				/// <summary>		/// The object responsible for updating the contents of the search results controller.		/// </summary>		[iOSVersion(8)]		[Export("searchResultsUpdater")]		public UISearchResultsUpdating SearchResultsUpdater { get; set; }	}}