using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UISearchDisplayController_Class/index.html#//apple_ref/occ/cl/UISearchDisplayController	/// <summary>	/// A search display controller manages the display of a search bar, along with a table view that displays search results.	/// </summary>	[iOSVersion(3)]	public class UISearchDisplayController : NSObject	{		/// <summary>		/// Returns a display controller initialized with the given search bar and contents controller.		/// </summary>		/// <param name="searchBar">A search bar.   The search bar must not currently be associated with another search display controller.</param>		/// <param name="contentsController">The view controller that manages display of the original contents that are to be searched.   The view controller must not currently be associated with another search display controller.</param>		/// <returns>A search display controller initialized with the given search bar and contents controller.</returns>		[iOSVersion(3)]		public UISearchDisplayController(UISearchBar searchBar, UIViewController contentsController) { }				/// <summary>		/// The visibility state of the search interface.		/// </summary>		[iOSVersion(3)]		[Export("active")]		public bool Active { get; set; }				/// <summary>		/// Displays or hides the search interface, optionally with animation.		/// </summary>		/// <param name="visible">true to display the search interface if it is not already displayed; false to hide the search interface if it is currently displayed.</param>		/// <param name="animated">true to use animation for a change in visible state, otherwise false.</param>		[iOSVersion(3)]		[Export("setActive")]		public void SetActive(bool visible, bool animated) { }				/// <summary>		/// The controller’s delegate.		/// </summary>		[iOSVersion(3)]		[Export("delegate")]		public UISearchDisplayDelegate Delegate { get; set; }				/// <summary>		/// The search bar. (read-only)		/// </summary>		[iOSVersion(3)]		[Export("searchBar")]		public UISearchBar SearchBar { get; private set; }				/// <summary>		/// The view controller that manages the contents being searched. (read-only)		/// </summary>		[iOSVersion(3)]		[Export("searchContentsController")]		public UIViewController SearchContentsController { get; private set; }				/// <summary>		/// The table view in which the search results are displayed. (read-only)		/// </summary>		[iOSVersion(3)]		[Export("searchResultsTableView")]		public UITableView SearchResultsTableView { get; private set; }				/// <summary>		/// The data source for the table view in which the search results are displayed.		/// </summary>		[iOSVersion(3)]		[Export("searchResultsDataSource")]		public UITableViewDataSource SearchResultsDataSource { get; set; }				/// <summary>		/// The delegate for the table view in which the search results are displayed.		/// </summary>		[iOSVersion(3)]		[Export("searchResultsDelegate")]		public UITableViewDelegate SearchResultsDelegate { get; set; }				/// <summary>		/// The title for the search results view.		/// </summary>		[iOSVersion(5)]		[Export("searchResultsTitle")]		public string SearchResultsTitle { get; set; }				/// <summary>		/// Specifies that the navigation bar contains a search bar.		/// </summary>		[iOSVersion(7)]		[Export("displaysSearchBarInNavigationBar")]		public bool DisplaysSearchBarInNavigationBar { get; set; }				/// <summary>		/// Represents the search display controller in a navigation controller’s navigation bar. (read-only)		/// </summary>		[iOSVersion(7)]		[Export("navigationItem")]		public UINavigationItem NavigationItem { get; private set; }	}}