using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITableViewController_Class/index.html#//apple_ref/occ/cl/UITableViewController	/// <summary>	/// The UITableViewController class creates a controller object that manages a table view. It implements the following behavior:	/// </summary>	[iOSVersion(2)]	public class UITableViewController : UIViewController	{		/// <summary>		/// Initializes a table-view controller to manage a table view of a given style.		/// </summary>		/// <param name="style">A constant that specifies the style of table view that the controller object is to manage (UITableViewStylePlain or UITableViewStyleGrouped).</param>		/// <returns>An initialized UITableViewController object or nil if the object couldnâ€™t be created.</returns>		[iOSVersion(2)]		public UITableViewController(UITableViewStyle style) { }				/// <summary>		/// Returns the table view managed by the controller object.		/// </summary>		[iOSVersion(2)]		[Export("tableView")]		public UITableView TableView { get; set; }				/// <summary>		/// A Boolean value indicating if the controller clears the selection when the table appears.		/// </summary>		[iOSVersion(3.2)]		[Export("clearsSelectionOnViewWillAppear")]		public bool ClearsSelectionOnViewWillAppear { get; set; }				/// <summary>		/// The refresh control used to update the table contents.		/// </summary>		[iOSVersion(6)]		[Export("refreshControl")]		public UIRefreshControl RefreshControl { get; set; }	}}