using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITapGestureRecognizer_Class/index.html#//apple_ref/occ/cl/UITapGestureRecognizer	/// <summary>	/// UITapGestureRecognizer is a concrete subclass of UIGestureRecognizer that looks for single or multiple taps. For the gesture to be recognized, the specified number of fingers must tap the view a specified number of times.	/// </summary>	[iOSVersion(3.2)]	public class UITapGestureRecognizer : UIGestureRecognizer	{		/// <summary>		/// The number of taps for the gesture to be recognized.		/// </summary>		[iOSVersion(3.2)]		[Export("numberOfTapsRequired")]		public int NumberOfTapsRequired { get; set; }				/// <summary>		/// The number of fingers required to tap for the gesture to be recognized.		/// </summary>		[iOSVersion(3.2)]		[Export("numberOfTouchesRequired")]		public int NumberOfTouchesRequired { get; set; }	}}