using ObjectiveC;using System;using SwiftSharp.Attributes;using Foundation;namespace UIKit{	//https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITextInputMode_Class/index.html#//apple_ref/occ/cl/UITextInputMode	/// <summary>	/// An instance of the UITextInputMode class represents the current text-input mode. You can use this object to determine the primary language currently being used for text input.	/// </summary>	[iOSVersion(4.2)]	public class UITextInputMode : NSObject	{		/// <summary>		/// Returns an instance representing the current text-input mode.		/// </summary>		/// <returns>An object representing the current input mode or nil if this object is not set by the text input system.</returns>		[iOSVersion(4.2)]		[Export("currentInputMode")]		public static UITextInputMode CurrentInputMode() { return null; }				/// <summary>		/// Returns the active text-input modes.		/// </summary>		[iOSVersion(5)]		[Export("activeInputModes")]		public static AnyObject[] ActiveInputModes() { return null; }				/// <summary>		/// The primary language, if any, of the input mode.		/// </summary>		[iOSVersion(4.2)]		[Export("primaryLanguage")]		public string PrimaryLanguage { get; private set; }								WHAT	}}