using ObjectiveC;using System;using SharpSwift.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// An instance of the UITextInputMode class represents the current text-input mode. You can use this object to determine the primary language currently being used for text input.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITextInputMode_Class/index.html#//apple_ref/occ/cl/UITextInputMode"/>    [iOSVersion(4.2)]    public class UITextInputMode : NSObject//, AnyObject, NSCoding, NSObjectProtocol, NSSecureCoding    {        /// <summary>        /// Returns the active text-input modes.        /// </summary>        [iOSVersion(5)]        [Export("activeInputModes")]        public static AnyObject[] ActiveInputModes() { return default(AnyObject[]); }                /// <summary>        /// The primary language, if any, of the input mode.        /// </summary>        [iOSVersion(4.2)]        [Export("primaryLanguage")]        public string PrimaryLanguage { get; private set; }    }    /// <summary>    /// The posting object is a UITextInputMode instance.    /// </summary>    [iOSVersion(4.2)]    public class UITextInputCurrentInputModeDidChangeNotification : NSNotification    {        public UITextInputCurrentInputModeDidChangeNotification() : base("", null) { }    }}