using ObjectiveC;using System;using SwiftSharp.Attributes;using System.Collections.Generic;using Foundation;using CoreGraphics;namespace UIKit{    /// <summary>    /// A UITraitCollection object provides details about the characteristics of a UIViewController object, which manages a set of views that make up a portion of your appâ€™s interface. These characteristics, or traits, define the size class, display scale, and device idiom of the view controller. When a view controller is created, a trait collection is automatically created for that view controller.    /// </summary>    /// <see cref="https://developer.apple.com/library/prerelease/ios/documentation/UIKit/Reference/UITraitSet_ClassReference/index.html#//apple_ref/occ/cl/UITraitCollection"/>    [iOSVersion(8)]    public class UITraitCollection : NSObject//, AnyObject, NSCoding, NSCopying, NSObjectProtocol, NSSecureCoding    {        /// <summary>        /// Returns a new trait collection containing only the indicated scale.        /// </summary>        /// <param name="displayScale">The scale for the new trait collection. Use 1.0 for non-Retina devices and 2.0 for Retina devices.</param>        [iOSVersion(8)]        public UITraitCollection(CGFloat displayScale) { }                /// <summary>        /// Returns a trait collection created using the traits contained within the specified array.        /// </summary>        /// <param name="traitsFromCollections">An array of UITraitCollection objects.</param>        [iOSVersion(8)]        public UITraitCollection(AnyObject[] traitsFromCollections) { }                /// <summary>        /// Creates a new trait collection containing only the indicated interface idiom.        /// </summary>        /// <param name="userInterfaceIdiom">A UIUserInterfaceIdiom value indicating the interface idiom to be used for the new trait collection.</param>        [iOSVersion(8)]        public UITraitCollection(UIUserInterfaceIdiom userInterfaceIdiom) { }                /// <summary>        /// Creates a new trait collection containing only the indicated size class.        /// </summary>        /// <param name="horizontalSizeClass">A UIUserInterfaceSizeClass value indicating the size class to be used for the new trait collection.</param>        [iOSVersion(8)]        public UITraitCollection(UIUserInterfaceSizeClass horizontalSizeClass) { }                /// <summary>        /// Creates a new trait collection containing only the indicated size class.        /// </summary>        /// <param name="verticalSizeClass">A UIUserInterfaceSizeClass value indicating the new size class to be used for the new trait collection.</param>        /// <param name="NAME_YOUR_PARAMS">DO NOT USE THIS PARAMETER - Instead make sure to name the parameters you're using.</param>        [iOSVersion(8)]        [IgnoreParameter("NAME_YOUR_PARAMS")]        public UITraitCollection(UIUserInterfaceSizeClass verticalSizeClass, bool NAME_YOUR_PARAMS = false) { }                /// <summary>        /// Returns a Boolean value that indicates whether a view contains a specific set of traits.        /// </summary>        /// <param name="trait">A UITraitCollection object containing a specific set of traits.</param>        [iOSVersion(8)]        [Export("containsTraitsInCollection")]        public bool ContainsTraitsInCollection(UITraitCollection trait) { return default(bool); }                /// <summary>        /// The horizontal size class for the view. (read-only)        /// </summary>        [iOSVersion(8)]        [Export("horizontalSizeClass")]        public UIUserInterfaceSizeClass HorizontalSizeClass { get; private set; }                /// <summary>        /// The current scale used by the display. (read-only)        /// </summary>        [iOSVersion(8)]        [Export("displayScale")]        public CGFloat DisplayScale { get; private set; }                /// <summary>        /// The current device idiom. (read-only)        /// </summary>        [iOSVersion(8)]        [Export("userInterfaceIdiom")]        public UIUserInterfaceIdiom UserInterfaceIdiom { get; private set; }                /// <summary>        /// The vertical size class for the view. (read-only)        /// </summary>        [iOSVersion(8)]        [Export("verticalSizeClass")]        public UIUserInterfaceSizeClass VerticalSizeClass { get; private set; }    }}